import logo from './logo.svg';
import { useState } from 'react';
import { ethers } from 'ethers';
import './App.css';



function App() {
  const [walletAddress, setWalletAddress] = useState('');
  const [walletScan, setWalletScan] = useState({
    network: "",
    balance: ""
  });

  const [table_data, setTableData]=useState([]);;

  function toFixedValue( value, dp ){
    return +parseFloat(value).toFixed( dp );
  }
  async function scanWallet() {
    const endpoint = {
      url: process.env.REACT_APP_NODE_ENDPOINT,
      user: process.env.REACT_APP_NODE_USERNAME,
      password: process.env.REACT_APP_NODE_PASSWORD,
    }
    if (ethers.utils.isAddress(walletAddress)) {
      const provider = new ethers.providers.JsonRpcProvider(endpoint, 'homestead');
      const network_obj=await provider.getNetwork();

      const network=network_obj.name;
      const preformat_balance = ethers.utils.formatEther(await provider.getBalance(walletAddress));
      const balance=toFixedValue(preformat_balance,4)
      setWalletScan({
        network,
        balance
      });

      console.log("network", walletScan.network)
      const body=JSON.stringify({
        "method": "coinbaseCloud_getBalances",
        "params": {
            "addressAndContractList": [
                {
                    "address": walletAddress,
                    "contract": "0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2"
                },
                {
                    "address": walletAddress,
                    "contract": "0x4d224452801ACEd8B2F0aebE155379bb5D594381"
                }
            ],
            "blockchain": "Ethereum",
            "network": "Mainnet"
        },
        "id": 1,
        "jsonrpc": "2.0"
    });
    console.log(body)
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: body
  };
  fetch('https://mainnet.ethereum.coinbasecloud.net', requestOptions)
        .then(response => console.log("response",response.json()));

      const table_data = [
        { token_name: "Ethereum", balance: balance},
        { token_name: "APECoin", balance: 1.2343},
        { token_name: "Wrapped Ether", balance: 12},
        { token_name: "USDC", balance: 10000}
      ]
      setTableData(table_data);

    } else {
      alert("Please enter the correct eth address");
    }



  }

  const setWalletAddr = event => {
    setWalletAddress(event.target.value);
  };

  const handleSubmit = event => {
    event.preventDefault();
    // üëáÔ∏è value of input field
    scanWallet();
    console.log("table data is", typeof(table_data));
    console.log('WalletAddress ', walletAddress);

  };




  return (
    <div className="App">
      <header className="App-header">
        <h3>Wallet Address Entered: {walletAddress}</h3>
        <h3>Balance: {walletScan.balance} ETH</h3>
        <h3>Network Name: {walletScan.network}</h3>
        <div className="form-style-6">
          <h1>Wallet Scanner</h1>
          <form onSubmit={handleSubmit}>
            <input id="walletAddress" type="text" name="walletAddress" value={walletAddress} onChange={setWalletAddr} />
            <input type="submit" value="Send" />
          </form>
        </div>
        <h3> Balances: </h3>
        <table className="styled-table">
        <thead>
          <tr>
            <th>Token Name</th>
            <th>Balance</th>
          </tr>
        </thead>
        <tbody>
          {
            table_data.map((value, key) => {
              return (
                <tr key={key}>
                  <td>{value.token_name}</td>
                  <td>{value.balance}</td>
                </tr>
              )
            })
          }
        </tbody>
      </table>

      </header>
    </div>
  );
}

export default App;
