import logo from './logo.svg';
import { useState } from 'react';
import { ethers } from 'ethers';
import './App.css';

function App() {


  const [walletAddress, setWalletAddress] = useState("");

  async function scanWallet() {
    const endpoint = {
      url: process.env.REACT_APP_NODE_USERNAME,
      user: process.env.REACT_APP_NODE_PASSWORD,
      password: process.env.REACT_APP_NODE_ENDPOINT
    }
    const provider = new ethers.providers.JsonRpcProvider(endpoint, 'homestead');

    console.log(walletAddress);


  }

  return (
    <div className="App">
      <header className="App-header">

        <div class="form-style-6">
          <h1>Wallet Scanner</h1>
          <form onSubmit={scanWallet}>
            <input type="text" name="walletaddress" placeholder="Your Wallet Address" onChange={(walletName)=>setWalletAddress(walletName.target.value)}/>
            <input type="submit" value="Send" />
          </form>
        </div>

      </header>
    </div>
  );
}

export default App;
