import logo from './logo.svg';
import { useState } from 'react';
import { ethers } from 'ethers';
import './App.css';

function App() {


  /* const [walletState, setWalletState] = useState({
    walletAddress: "",
    network: ""
  }); */

  const [walletAddress, setWalletAddress] = useState('');
  const [walletScan, setWalletScan] = useState({
    network: "",
    balance: ""
  });
  function toFixedValue( value, dp ){
    return +parseFloat(value).toFixed( dp );
  }
  async function scanWallet() {
    const endpoint = {
      url: process.env.REACT_APP_NODE_ENDPOINT,
      user: process.env.REACT_APP_NODE_USERNAME,
      password: process.env.REACT_APP_NODE_PASSWORD,
    }
    if (ethers.utils.isAddress(walletAddress)) {
      const provider = new ethers.providers.JsonRpcProvider(endpoint, 'homestead');
      const network=await provider.getNetwork();
      const preformat_balance = ethers.utils.formatEther(await provider.getBalance(walletAddress));
      const balance=toFixedValue(preformat_balance,4)

      setWalletScan({
        network,
        balance
      });

      console.log("balance", walletScan.balance)

    } else {
      alert("Please enter the correct eth address")
    }

    //const network_name = await provider.getNetwork();

  }

  const setWalletAddr = event => {
    setWalletAddress(event.target.value);
  };

  const handleSubmit = event => {
    event.preventDefault();
    // üëáÔ∏è value of input field
    scanWallet();
    console.log('WalletAddress ', walletAddress);

  };




  return (
    <div className="App">
      <header className="App-header">
        <h2>Wallet Address Entered: {walletAddress}</h2>
        <h3>Balance: {walletScan.balance} ETH</h3>
        <div className="form-style-6">
          <h1>Wallet Scanner</h1>
          <form onSubmit={handleSubmit}>
            <input id="walletAddress" type="text" name="walletAddress" value={walletAddress} onChange={setWalletAddr} />
            <input type="submit" value="Send" />
          </form>
        </div>
      </header>
    </div>
  );
}

export default App;
