import logo from './logo.svg';
import { useState } from 'react';
import { ethers } from 'ethers';
import './App.css';

function App() {


  const [walletAddress, setWalletAddress] = useState("");
  const [network, setNetworkName]=useState("");

  async function scanWallet(walletName) {
    const endpoint = {
      url: process.env.REACT_APP_NODE_USERNAME,
      user: process.env.REACT_APP_NODE_PASSWORD,
      password: process.env.REACT_APP_NODE_ENDPOINT
    }
    const provider = new ethers.providers.JsonRpcProvider(endpoint, 'homestead');
    const network_name = await provider.getNetwork();
    setNetworkName(network_name)
    console.log(walletAddress);


  }

  let handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("https://httpbin.org/post", {
        method: "POST",
        body: JSON.stringify({
          name: name,
          email: email,
          mobileNumber: mobileNumber,
        }),
      });
      let resJson = await res.json();
      if (res.status === 200) {
        setName("");
        setEmail("");
        setMessage("User created successfully");
      } else {
        setMessage("Some error occured");
      }
    } catch (err) {
      console.log(err);
    }
  };



  return (
    <div className="App">
      <header className="App-header">


      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          placeholder="Name"
          onChange={(e) => setName(e.target.value)}
        />
        <button type="submit">Create</button>
        </form>

        <div className="form-style-6">
          <h1>Wallet Scanner</h1>
          <form onSubmit={scanWallet}>
            <input type="text" name="walletaddress" placeholder="Your Wallet Address" onChange={()=>setWalletAddress(walletAddress)}/>
            <input type="submit" value="Send" />
          </form>
        </div>

      </header>
    </div>
  );
}

export default App;
