import logo from './logo.svg';
import { useState } from 'react';
import { ethers } from 'ethers';
import './App.css';

function App() {


  /* const [walletState, setWalletState] = useState({
    walletAddress: "",
    network: ""
  }); */
  const [message, setMessage] = useState('');

  const [walletAddress, setWalletAddress] = useState('');

  const handleChange = event => {
    setMessage(event.target.value);
  };

  const handleClick = event => {
    event.preventDefault();
    // üëáÔ∏è value of input field
    console.log('old value: ', message);
    // üëáÔ∏è set value of input field
    setMessage('New value');
  };

  async function scanWallet(walletName) {
    const endpoint = {
      url: process.env.REACT_APP_NODE_USERNAME,
      user: process.env.REACT_APP_NODE_PASSWORD,
      password: process.env.REACT_APP_NODE_ENDPOINT
    }
    const provider = new ethers.providers.JsonRpcProvider(endpoint, 'homestead');
    const network_name = await provider.getNetwork();

  }
  const handleThisClick = event => {
    event.preventDefault();
    // üëáÔ∏è value of input field
    console.log('old value: ', message);
    // üëáÔ∏è set value of input field
    setMessage('New value');
  };

  const handleSubmit = event => {
    event.preventDefault();
    // üëáÔ∏è value of input field
    console.log('WalletAddress ', walletAddress);
    // üëáÔ∏è set value of input field
    setMessage('New value');
  };




  return (
    <div className="App">
      <header className="App-header">

        <div className="form-style-6">
          <h1>Wallet Scanner</h1>
          <form >
            <input id="walletAddress" type="text" name="walletAddress" value={walletAddress}/>
            <input type="submit" value="Send" />
          </form>
        </div>

        <div>
      <input
        type="text"
        id="message"
        name="message"
        onChange={handleChange}
        value={message}
      />

      <h2>Message: {message}</h2>

      <button onClick={handleClick}>Click</button>
    </div>


      </header>
    </div>
  );
}

export default App;
