import { useState } from 'react';
import { ethers } from 'ethers';
import { CoinbaseCloud, Network } from '@coinbase/coinbase-cloud-sdk';
import './App.css';



function App() {
  const [walletAddress, setWalletAddress] = useState('');
  const [walletScan, setWalletScan] = useState({
    network: "",
    balance: "",
    latest_block: ""
  });
  const [table_data, setTableData] = useState([]);;

  function toFixedValue(value, dp) {
    return +parseFloat(value).toFixed(dp);
  }

  function payLoad_erc20Tokens(walletAddress, contract_list) {
    let tmp;
    var address_contract_list = []
    for (var i = 0; i < contract_list.length; i++) {
      tmp = { "address": walletAddress, "contract": contract_list[i] }
      address_contract_list.push(tmp)
    }
    return address_contract_list
  }
  function diff_timestamp(timestamp1, timestamp2) {
    var difference = timestamp1 - timestamp2;
    console.log("difference is", difference)
    var daysDifference = Math.floor(difference/1000/60/60/24);

    return daysDifference;
}

  function populateTransactionData(transaction_list){
    let tmp;
    var transaction_data = []
    for (var i = 0; i < transaction_list.length; i++) {
      let block_timestamp=parseInt(transaction_list[i].blockTimestamp);
      console.log(block_timestamp)
      let current_timestamp=Date.now();
      console.log(current_timestamp)
      let days_diff=diff_timestamp(block_timestamp,current_timestamp);
      console.log("time diff", days_diff)
      tmp = { "from": transaction_list[i].from, "to": transaction_list[i].to, "value": ethers.utils.formatEther(transaction_list[i].value), "hash": transaction_list[i].transactionHash, "block_number": transaction_list[i].blockNumber, "Age": days_diff }
      transaction_data.push(tmp)
    }
    return transaction_data
  }

  async function scanWallet() {

    const settings = {
      apiUsername: process.env.REACT_APP_NODE_USERNAME, // You can find this in your Node project's settings
      apiPassword: process.env.REACT_APP_NODE_PASSWORD, // Password associated with the username
      network: Network.ETH_MAINNET
    }


    if (ethers.utils.isAddress(walletAddress)) {
      const coinbaseCloud = new CoinbaseCloud(settings)
      const cbProvider = coinbaseCloud.provider

      const network_obj = await cbProvider.getNetwork();
      const latest_block = await cbProvider.getBlockNumber()


      console.log(ethers.utils.hexlify(latest_block))

      //transactions
      const transactions = await coinbaseCloud.advanced
        .getTransactionsByAddress(
          walletAddress,
          '0x0',//start block
          ethers.utils.hexlify(latest_block),//end block
          'SENDER_OR_RECEIVER',
          'desc',
          10,
          1
        );

      console.log("Verify my transactions", transactions.transactions)
      let transaction_list=transactions.transactions;
      console.log(populateTransactionData(transaction_list))



      const contract_list = ["0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2", "0x4d224452801ACEd8B2F0aebE155379bb5D594381", "0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48"]
      const contract_params = payLoad_erc20Tokens(walletAddress, contract_list);
      //call advanced API for erc20 tokens 
      const cb_balances = await coinbaseCloud.advanced.getBalances(contract_params)
      let token_balances = cb_balances.balances[0].tokenBalances


      console.log("latest is: ", latest_block, "Block -100", latest_block - 50000)


      const network = network_obj.name;
      const preformat_balance = ethers.utils.formatEther(await cbProvider.getBalance(walletAddress));

      const balance = toFixedValue(preformat_balance, 4)
      setWalletScan({
        network,
        balance,
        latest_block
      });

      let wrappedEthBalance;
      let apeCoinBalance;
      let usdcBalance;
      for (const eachTokenBalance in token_balances) {
        if (token_balances[eachTokenBalance].contract === '0xc02aaa39b223fe8d0a0e5c4f27ead9083c756cc2') {
          const preWrappedCoin = token_balances[eachTokenBalance].amount.toString();
          wrappedEthBalance = ethers.utils.formatUnits(preWrappedCoin, token_balances[eachTokenBalance].decimals);
          wrappedEthBalance = toFixedValue(wrappedEthBalance, 4);
        }
        else if (token_balances[eachTokenBalance].contract === "0x4d224452801aced8b2f0aebe155379bb5d594381") {
          const preApeCoin = token_balances[eachTokenBalance].amount.toString();
          apeCoinBalance = ethers.utils.formatUnits(preApeCoin, token_balances[eachTokenBalance].decimals);
          apeCoinBalance = toFixedValue(apeCoinBalance, 4)
        }
        else if (token_balances[eachTokenBalance].contract === "0xa0b86991c6218b36c1d19d4a2e9eb0ce3606eb48") {
          const preusdcBalance = token_balances[eachTokenBalance].amount.toString();
          usdcBalance = ethers.utils.formatUnits(preusdcBalance, token_balances[eachTokenBalance].decimals);
          usdcBalance = toFixedValue(usdcBalance, 4)
        }
      }

      console.log("apecoin", apeCoinBalance);
      console.log("usdc", usdcBalance);
      console.log("wrapped", wrappedEthBalance);



      const table_data = [
        { token_name: "Ethereum", balance: balance },
        { token_name: "APECoin", balance: apeCoinBalance },
        { token_name: "Wrapped Ether", balance: wrappedEthBalance },
        { token_name: "USDC", balance: usdcBalance }
      ]
      setTableData(table_data);

    } else {
      alert("Please enter the correct eth address");
    }



  }


  const setWalletAddr = event => {
    setWalletAddress(event.target.value);
  };
  /*
  const setContractAddress = event => {
      setContractAddr(event.target.value);
    };
  */


  const handleSubmit = event => {
    event.preventDefault();
    // üëáÔ∏è value of input field
    scanWallet();
    console.log("table data is", typeof (table_data));
    console.log('WalletAddress ', walletAddress);

  };
  /*
    function handleContractSubmit(event) {
      event.preventDefault();
      // üëáÔ∏è value of input field
      console.log('ContractAddress ', contractAddress);
    }

  */



  return (
    <div className="App">
      <header className="App-header">
        <h3>Wallet Address Entered: {walletAddress}</h3>
        <h3>Network Name: {walletScan.network}</h3>
        <h4>Latest Block: {walletScan.latest_block}</h4>
        <div className="form-style-6">
          <h1>Wallet Scanner</h1>
          <form onSubmit={handleSubmit}>
            <input id="walletAddress" type="text" name="walletAddress" value={walletAddress} onChange={setWalletAddr} />
            <input type="submit" value="Send" />
          </form>
        </div>

        <div className="row">
          <div className="column">
            <h3> Balances: </h3>
            <table className="styled-table">
              <thead>
                <tr>
                  <th>Token Name</th>
                  <th>Balance</th>
                </tr>
              </thead>
              <tbody>
                {
                  table_data.map((value, key) => {
                    return (
                      <tr key={key}>
                        <td>{value.token_name}</td>
                        <td>{value.balance}</td>
                      </tr>
                    )
                  })
                }
              </tbody>
            </table>
          </div>
          <div className="column">
            <h3> Transactions: </h3>
            <table className="styled-table">

              <thead>
                <tr>
                  <th>First Name</th>
                  <th>Last Name</th>
                  <th>Points</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Jill</td>
                  <td>Smith</td>
                  <td>50</td>
                </tr>
                <tr>
                  <td>Eve</td>
                  <td>Jackson</td>
                  <td>94</td>
                </tr>
                <tr>
                  <td>Adam</td>
                  <td>Johnson</td>
                  <td>67</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
